function testData(){
	var testInput="4x3x2\n1x1x10";
	var boxes=createBoxArray(testInput);
	var size=getWrappingPaperSize(boxes);
	console.log(size);
}

function getRibbonSize(boxArray){
	var size=0;
	for(var i=0;i<boxArray.length;i++){
		size+=boxArray[i].getSmallestSide();
		size+=boxArray[i].getCubicFeet();
	}
	return size;
}

function getWrappingPaperSize(boxArray){
	var size=0;
	for(var i=0;i<boxArray.length;i++){
		size+=boxArray[i].getOutSideDimensions();
		size+=boxArray[i].getMinimumSide();
	}
	return size;
}

function compareFunc(a,b) {
	if (a<b){
		return -1;
	}
	if (a>b) {
		return 1;
	}
	return 0;
}

function createBoxArray(inputString){
	var boxArrayString=inputString.split("\n");
	var boxArray = [];
	var curBox;
	for(var i=0;i<boxArrayString.length;i++){
		curBox=new Box(boxArrayString[i]);
		boxArray.push(curBox);
	}
	return boxArray;
}

function Box(input){
	this.boxDimensions = input.split("x");
	this.boxDimensions = this.boxDimensions.map(x => parseInt(x));
	this.boxDimensions.sort(compareFunc);
	console.log(this.boxDimensions);
	this.getMinimumSide = function(){
		return this.boxDimensions[0] * this.boxDimensions[1];
	}
	this.getOutSideDimensions = function() {
		var side1=this.boxDimensions[0] * this.boxDimensions[1];
		var side2=this.boxDimensions[1] * this.boxDimensions[2];
		var side3=this.boxDimensions[0] * this.boxDimensions[2];
		var size = 2*(side1+side2+side3);
		console.log(size);
		return size;
	}

	this.getSmallestSide = function(){
		return this.boxDimensions[0] + this.boxDimensions[1] + this.boxDimensions[0] + this.boxDimensions[1];
	}

	this.getCubicFeet = function() {

		return this.boxDimensions[0] * this.boxDimensions[1] * this.boxDimensions[2];
	}
}

function day1a(inputString) {
	var boxes=createBoxArray(inputString);
	console.log("Number of Boxes");
	console.log(boxes.length);
	var size=getWrappingPaperSize(boxes);
	console.log(size);
}

function day1b(inputString) {
	var boxes=createBoxArray(inputString);
	console.log("Number of Boxes");
	console.log(boxes.length);
	var size=getRibbonSize(boxes);
	console.log(size);
}

