function testData(){
	var directions1 = ">";
	var directions2 = "^>v<";
	var directions3 = "^v^v^v^v^v";

	var myGrid = new Grid();
	var myState = new State();
	deliverPresents(directions1.split("") ,myState, myGrid);
	console.log(myGrid.onCount())

	myGrid = new Grid();
	myState = new State();
	deliverPresents(directions2.split("") ,myState, myGrid);
	console.log(myGrid.onCount())

	myGrid = new Grid();
	myState = new State();
	deliverPresents(directions3.split("") ,myState, myGrid);
	console.log(myGrid.onCount())
}


function State(){
	this.x=0;
	this.y=0;
}

function deliverPresents(directions,currentState, currentGrid){
	currentGrid.set(currentState.x,currentState.y, currentGrid.get(currentState.x,currentState.y)+1);
	for(var i=0;i<directions.length;i++){
		moveAndLeavePresent(directions[i], currentState, currentGrid);
	}
}

function moveAndLeavePresent(direction, currentState, currentGrid){
	switch (direction){
		case "^":
			currentState.y++;
			break;
		case "v":
			currentState.y--;
			break;
		case ">":
			currentState.x++;
			break;
		case "<":
			currentState.x--;
			break;
	}
	console.log("Setting x: "+currentState.x + " y: "+currentState.y + " To the Value: " + currentGrid.get(currentState.x,currentState.y)+1);
	//currentGrid.set(currentState.x,currentState.y, currentGrid.get(currentState.x,currentState.y)+1);
	currentGrid.set(currentState.x,currentState.y, 1);
}


function day3a(directions){
	var myGrid = new Grid();
	var myState = new State();
	deliverPresents(directions.split("") ,myState, myGrid);
	console.log(myGrid.onCount())
}

function seperateArray(offset,mod){
	return function(element,index){
		return !((index+offset)%mod);
	}
}

function day3b(directions){
	var santaDirections = directions.split("").filter(seperateArray(0,2));
	var roboSanta = directions.split("").filter(seperateArray(1,2));

	var myGrid = new Grid();
	var myState = new State();
	deliverPresents(santaDirections ,myState, myGrid);
	var myState = new State();
	deliverPresents(roboSanta ,myState, myGrid);
	console.log(myGrid.onCount())
}

function Grid(){
	this.grid=[];

	this.onCount = function(){
		var count=0;
		var rowKeys = Object.keys(this.grid);
		var colKeys;
		for(var i=0;i<rowKeys.length;i++){
			colKeys = Object.keys(this.grid[rowKeys[i]]);
			for(var j=0;j<colKeys.length;j++){
				count++;
			}
		}
		return count;
	}

	this.getHeight = function(){
		return this.grid.length;
	}

 	this.setFromStringa = function(inputStringArray){
		 this.grid=[];

		 for(var i=0;i<inputStringArray.length;i++){
			 var curRow=inputStringArray[i].split("");
			 for(var j=0;j<curRow.length;j++){
				 if(curRow[j]==="#"){
					this.set(i,j,true);
				 } else {
					this.set(i,j,false);
				 }
			 }
		 }
	};

	this.setFromStringb = function(inputStringArray){
		this.grid=[];

		for(var i=0;i<inputStringArray.length;i++){
			var curRow=inputStringArray[i].split("");
			for(var j=0;j<curRow.length;j++){
//				console.log("Setting: "+curRow[j]);
				this.set(i,j,curRow[j]);
			}
		}
	};

	this.toStringa = function(){
		var curRow;
		for(var i=0;i<this.grid.length;i++){
			if(this.grid[i]){
				curRow="";
				for(var j=0;j<this.grid[i].length;j++){
					if(this.get(i,j)){
						curRow+="#";
					} else {
						curRow+=".";
					}
				}
//				console.log(curRow);
			}
		}
	}
	 
	this.toStringb = function(){
		var curRow;
		for(var i=0;i<this.grid.length;i++){
			if(this.grid[i]){
				curRow="";
				for(var j=0;j<this.grid[i].length;j++){
					curRow+=this.get(i,j);
				}
//				console.log(curRow);
			}
		}
	}
	this.get = function(x,y){
		if(!this.grid[x]){
			return 0; //first row doesn't exist.
		}
		return this.grid[x][y];
	}
	this.set = function(x,y,value){
		this.grid[x]=this.grid[x]||[];
		this.grid[x][y]=value;
	}

	this.flip = function(){
		var newGrid=new Grid();
		for(var i=0;i<this.grid.length;i++){
			// console.log("Flipping row"+i);
			newGrid.grid[i]=this.grid[i].slice(0).reverse();
		}
		return newGrid;
	}
	this.mirror = function(){
		var temp=new Grid();
		for(var i=0;i<this.grid.length;i++){
			for(var j=0;j<this.grid[i].length;j++){
				temp.set(j,i,this.get(i,j));
			}
		}
		return temp;
	}
	this.rotate = function(){
		var temp=this.mirror();
		return temp.flip();
	}

	this.isEqual = function(otherSide){
		if(otherSide.getHeight()!==this.getHeight()){
			return false;
		}
		for(var i=0;i<otherSide.getHeight();i++){
			if(otherSide.getHeight()!==this.getHeight()){
				return false;
			}
			for(var j=0;j<otherSide.grid[i].length;j++){
				if(this.get(i,j)!==otherSide.get(i,j)){
					return false;
				}
			}
		}
		return true;
	}
}

